import React, { useRef, useEffect, useState } from "react";
import axios from 'axios';
import { decode } from 'jpeg-js';
function Camera() {
  const videoRef = useRef(null);
  const photoRef = useRef(null);
  const [hasPhoto, setHasPhoto] = useState(false);
  const [facingMode, setFacingMode] = useState("user");
  const[plantname,setPlantname]= useState(" ");
  const [result,setResult] = useState("")
  const [cnnimg, setImgCNN]=useState("");
  const [imgfile,setImgfile]= useState([]);


  const getVideo = () => {
    navigator.mediaDevices
      .getUserMedia({
        video: { width: 256, height: 256, facingMode: facingMode }
      })
      .then((stream) => {
        let video = videoRef.current;
        video.srcObject = stream;
        video.play();
      })
      .catch((err) => {
        console.error(err);
      });
  };

  useEffect(() => {
    getVideo();
  }, [videoRef, facingMode]);

  const takePhoto = () => {
    const width = 256;
    const height = 256;

    let video = videoRef.current;
    let photo = photoRef.current;

    photo.width = width;
    photo.height = height;

    let ctx = photo.getContext("2d");
    ctx.drawImage(video, 0, 0, width, height);

    setHasPhoto(true);
  };

  const closePhoto = () => {
    let photo = photoRef.current;
    let ctx = photo.getContext("2d");

    ctx.clearRect(0, 0, photo.width, photo.height);

    setHasPhoto(false);
  };

  const switchCamera = () => {
    if (facingMode === "user") {
      setFacingMode("environment");
    } else {
      setFacingMode("user");
    }
  };

  function saveCanvasImage(canvasId, filename) {
    const canvas = document.getElementById(canvasId);
    const dataURL = canvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = dataURL;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }

  function handleSaveButtonClick() {
    saveCanvasImage("myCanvas", "myImage.png");
  }


  function submitCanvasImage(canvasId, filename) {
    const canvas = document.getElementById(canvasId);
    const dataURL = canvas.toDataURL('image/jpeg', 0.8);
    const blob = dataURLToBlob(dataURL, 'image/jpeg');
    const formData = new FormData();
    formData.append('image', blob, filename);

    function dataURLToBlob(dataURL) {
      var BASE64_MARKER = ';base64,';

      if (dataURL.indexOf(BASE64_MARKER) == -1) {
          var parts = dataURL.split(',');
          var contentType = parts[0].split(':')[1];
          var raw = decodeURIComponent(parts[1]);

          return new Blob([raw], {type: contentType});
      }

      var parts = dataURL.split(BASE64_MARKER);
      var contentType = parts[0].split(':')[1];
      var raw = window.atob(parts[1]);
      var rawLength = raw.length;

      var uInt8Array = new Uint8Array(rawLength);

      for (var i = 0; i < rawLength; ++i) {
          uInt8Array[i] = raw.charCodeAt(i);
      }

      return new Blob([uInt8Array], {type: contentType});
  }

    // Create a new FileReader object to read the file as an array buffer
    const reader = new FileReader();
    // Decode the JPEG image data and get the pixel values
    const imageData = decode(new Uint8Array(formData), { useTArray: true });
    var temp=[];
  var cnntemp = []
    // Loop over the pixels in the image data and read their values
    for (let y = 0; y < imageData.height; y++) {
      var line = []
      for (let x = 0; x < imageData.width; x++) {
        const index = (y * imageData.width + x) * 4;
        const red = imageData.data[index];
        const green = imageData.data[index + 1];
        const blue = imageData.data[index + 2];
        const alpha = imageData.data[index + 3];

        temp.push(blue);
        line.push([red,green,blue]);

        // Do something with the pixel data here
        // console.log(`Pixel at (${x}, ${y}): (${red}, ${green}, ${blue}, ${alpha})`);
      }
      cnntemp.push(line)
    }
    setImgfile(temp)
    setImgCNN(cnntemp);

    onFileUpload();

    // fetch('https://example.com/submit-form', {
    //   method: 'POST',
    //   body: formData,
    // })
    // .then(response => {
    //   // Handle response from server
    // })
    // .catch(error => {
    //   // Handle error
    // });
  }

  const  onFileUpload = () => {
    axios.post("http://127.0.0.1:8000/gettomoato/",{imagefile:imgfile,CNNImg:cnnimg})
.then(data=>{

    alert("The "+plantname+" Plant is" + data.data.result)
    setResult("The "+plantname+" Plant is" + data.data.result)

})


  };





  return (
    <div className="CameraApp">
      <div className="Camera">
        <video ref={videoRef}></video>
        <p>
          <button className="btn" onClick={takePhoto}>
            Capture
          </button></p>
          <p><button className="btn" onClick={switchCamera}>
            Switch Camera
          </button></p>
          </div>
      <div className={'result' + (hasPhoto ? 'hasPhoto' : '')}>
      <canvas id="myCanvas" ref={photoRef}></canvas>
      <p><button className='btn' onClick={closePhoto}>Re-Capture</button></p>
      <p><button className='btn' onClick={handleSaveButtonClick}>Download</button></p>
      <p><button className='btn' value="Submit" onClick={()=>{submitCanvasImage()}}>Submit</button></p>

      </div>
    </div>
  );
}
export default Camera;
